mapper.insert:
INSERT INTO favorite_user_news (
user_id,
news_id
) VALUES (
#{userId},
#{newsId}
)

mapper.update:
update news
set
  title = #{title},
  coverUrl = #{coverUrl},
  amount = #{amount}
where id = #{id}

mapper.select:
select user_id, news_id, create_time
from like_user_news
WHERE user_id = #{userId} AND news_id = #{newsId}

select user_id, news_id
from favorite_user_news
WHERE user_id = #{userId} AND news_id = #{newsId}

select count(1)
from favorite_user_news
WHERE  news_id = #{newsId}

select count(1)
from like_user_news
WHERE  news_id = #{newsId}
