mapper.select:
select id, travel_id, title, subTitle, coverUrl, schedule, type
from travelcommend
where id = #{id}

select id, travel_id t_id, title, subTitle, coverUrl, schedule, type
from travelcommend


    type = #{type}

select tc.*, t.id t_id, u.headImgUrl u_headImgUrl,u.nickName u_nickName,u.id u_id,r.id r_id,r.name r_name from travelcommend tc left join travel t on tc.travel_id = t.id left join region r on t.place_id = r.id left join user u on t.author_id = u.id
and tc.type = #{type}
and tc.schedule <= now()
